import { React, useState, useEffect } from 'react';
import { MapContainer, TileLayer, Circle, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import styled from 'styled-components';
import PropTypes from 'prop-types';

function Map({ className }) {
    const center = [13.7563, 100.5018]; // Thailand's coordinates
    const zoomLevel = 6; // Initial zoom level

    const customMarkerIcon = L.divIcon({
        className: 'custom-marker-icon',
        html: '<div style="background-color: red; width: 12px; height: 12px; border-radius: 50%;"></div>',
    });

    const [userLocation, setUserLocation] = useState(null);
    const [zoom, setZoom] = useState(zoomLevel);

    useEffect(() => {
        // Function to fetch the user's location when the component mounts
        const fetchUserLocation = () => {
            if ('geolocation' in navigator) {
                navigator.geolocation.getCurrentPosition((position) => {
                    const { latitude, longitude } = position.coords;
                    setUserLocation([latitude, longitude]);
                    setZoom(12); // Set zoom level
                });
            } else {
                alert('Geolocation is not supported in your browser.');
            }
        };

        // Fetch the user's location when the component mounts
        fetchUserLocation();
    }, []);

    // Function to handle clicking the "Go to My Location" button
    const handleGoToUserLocation = () => {
        if ('geolocation' in navigator) {
            navigator.geolocation.getCurrentPosition((position) => {
                const { latitude, longitude } = position.coords;
                setUserLocation([latitude, longitude]);
                setZoom(12); // Set zoom level
            });
        } else {
            alert('Geolocation is not supported in your browser.');
        }
    };

    return (
        <map className={className}>
            <div className="playground">
                <button className='my-location-btn' onClick={handleGoToUserLocation}>Go to My Location</button>
                <div className='info'>
                    <MapContainer center={userLocation || center} zoom={zoom} className="MapContainer">
                        <TileLayer
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        />
                        {userLocation && (
                            <>
                                <Circle center={userLocation} radius={100} color="red">
                                    <Popup>You are here</Popup>
                                </Circle>
                                <Marker position={userLocation} icon={customMarkerIcon}>
                                    <Popup>Center</Popup>
                                </Marker>
                            </>
                        )}
                    </MapContainer>
                </div>
            </div>
        </map>
    );
}

Map.propTypes = {
    className: PropTypes.string.isRequired
}

export default styled(Map)`
.MapContainer {
    width: 100%;
    height: 1000px;
}
.info {
    /* margin-top: 65px; */
}
.playground {
    width: 100%;
}
.my-location-btn {
    margin-left: 50%;
}

.custom-marker-icon {
    width: 12px;
    height: 12px;
    margin: -6px 0 0 -6px; /* Adjust margin for centering */
    background-color: red;
    border-radius: 50%;
}
`
